import { Heading3 } from "./headings";

function Main(props) {
  const [title, setTitle] = useState(props.children[0].title)
  const [content, setContent] = useState(props.children[0].title)

  return (
    <main className="grid grid-main">
      <Heading3 children={title} />
      <article>
        <p>
          {content}
        </p>
      </article>
    </main>
  );
}

export default Main;
